    if ($scope.ev) {
      if ($scope.ev.in_promotion) {

        var end_date = $scope.convertToUTC($scope.ev.promotion.end_time, "UTC");

        if (end_date >= currentDate) {
          return (true);
        }
      }
    }
    return (false);
